    int AnswerCount(int aId)
    {
        var count = 0;
        foreach (var answer in Model.Choices)
        {
            if (answer.AnswerId == aId)
            {
                count += 1;
            }
        }
        return count;
    }

    double MedianScore()
    {
        var sum = 0;
        var count = 0;
        foreach (var game in Model.Games)
        {
            count += 1;
            sum += game.Points;
        }
        return (double) sum / count!;
    }

